create table "public"."cod_adventures" (
    "id" bigint generated by default as identity not null,
    "name" text not null,
    "short_description" text not null,
    "image_url" text,
    "looking_for_players" boolean not null default false,
    "is_active" boolean not null default false,
    "dg_master_id" uuid not null,
    "slug" text not null default ''::text,
    "created_at" timestamp with time zone not null default now()
);


alter table "public"."cod_adventures" enable row level security;

create table "public"."adk_available_systems" (
    "id" bigint generated by default as identity not null,
    "name" text not null,
    "short_description" text not null,
    "image_url" text,
    "slug" text not null,
    "is_active" boolean not null default false
);


alter table "public"."adk_available_systems" enable row level security;

create table "public"."adk_user_profiles" (
    "id" uuid not null,
    "name" text not null,
    "user_name" text,
    "email" text not null,
    "avatar_url" text not null,
    "primary_system_id" bigint
);


alter table "public"."adk_user_profiles" enable row level security;

CREATE UNIQUE INDEX cod_adventures_pkey ON public.cod_adventures USING btree (id);

CREATE UNIQUE INDEX cod_adventures_slug_key ON public.cod_adventures USING btree (slug);

CREATE UNIQUE INDEX adk_available_systems_pkey ON public.adk_available_systems USING btree (id);

CREATE UNIQUE INDEX adk_user_profiles_pkey ON public.adk_user_profiles USING btree (id);

alter table "public"."cod_adventures" add constraint "cod_adventures_pkey" PRIMARY KEY using index "cod_adventures_pkey";

alter table "public"."adk_available_systems" add constraint "adk_available_systems_pkey" PRIMARY KEY using index "adk_available_systems_pkey";

alter table "public"."adk_user_profiles" add constraint "adk_user_profiles_pkey" PRIMARY KEY using index "adk_user_profiles_pkey";

alter table "public"."cod_adventures" add constraint "cod_adventures_dg_master_id_fkey" FOREIGN KEY (dg_master_id) REFERENCES auth.users(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."cod_adventures" validate constraint "cod_adventures_dg_master_id_fkey";

alter table "public"."cod_adventures" add constraint "cod_adventures_slug_key" UNIQUE using index "cod_adventures_slug_key";

alter table "public"."adk_user_profiles" add constraint "adk_user_profiles_id_fkey" FOREIGN KEY (id) REFERENCES auth.users(id) ON UPDATE CASCADE ON DELETE CASCADE not valid;

alter table "public"."adk_user_profiles" validate constraint "adk_user_profiles_id_fkey";

alter table "public"."adk_user_profiles" add constraint "adk_user_profiles_primary_system_id_fkey" FOREIGN KEY (primary_system_id) REFERENCES adk_available_systems(id) ON UPDATE CASCADE ON DELETE SET NULL not valid;

alter table "public"."adk_user_profiles" validate constraint "adk_user_profiles_primary_system_id_fkey";

set check_function_bodies = off;

CREATE OR REPLACE FUNCTION public.insert_new_user_profile_data()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$BEGIN
  insert into public.adk_user_profiles(id, name, user_name, email, avatar_url, primary_system_id) values(new.id, new.raw_user_meta_data->>'name', new.raw_user_meta_data->>'user_name', new.raw_user_meta_data->>'email', new.raw_user_meta_data->>'avatar_url', NULL);
  return new;
END;$function$
;

create trigger create_user_profile_on_signup after insert on auth.users for each row execute function insert_new_user_profile_data();

