create policy "Allows dungeon master to create adventures on their account"
on "public"."cod_adventures"
as permissive
for insert
to authenticated
with check ((( SELECT auth.uid() AS uid) = dg_master_id));


create policy "Enable dungeon masters to delete their own adventures"
on "public"."cod_adventures"
as permissive
for delete
to authenticated
using ((( SELECT auth.uid() AS uid) = dg_master_id));


create policy "Enable read access for all users"
on "public"."cod_adventures"
as permissive
for select
to authenticated
using (true);


create policy "Enables dungeon masters to update their own adventures"
on "public"."cod_adventures"
as permissive
for update
to authenticated
using ((( SELECT auth.uid() AS uid) = dg_master_id))
with check ((( SELECT auth.uid() AS uid) = dg_master_id));


create policy "Enable read access for all authenticated users"
on "public"."adk_available_systems"
as permissive
for select
to authenticated
using (true);


create policy "Enable users to update their own profile"
on "public"."adk_user_profiles"
as permissive
for update
to public
using ((( SELECT auth.uid() AS uid) = id));


create policy "Enable users to view profiles"
on "public"."adk_user_profiles"
as permissive
for select
to authenticated
using (true);

create policy "Read/Write access to adventure_covers folder mjfl0w_0"
on "storage"."objects"
as permissive
for select
to authenticated
using (((bucket_id = 'adventure-keeper-data-prod'::text) AND ((storage.foldername(name))[1] = 'adventure_covers'::text) AND (auth.role() = 'authenticated'::text)));


